    <script>
        /* --- Data & State --- */
        const basePrice = 19.90; // Price for 500g

        let currentProduct = {
            name: "æ–°ç–†åŸäº§å¤§é¢—ç²’é¦™ç“œå­",
            flavor: "ç»å…¸åŸå‘³",
            weight: "è¢‹è£… 500g",
            weightMultiplier: 1,
            price: basePrice,
            qty: 1,
            img: document.getElementById('mainImg').src
        };

        // Initialize Cart from LocalStorage
        let cart = JSON.parse(localStorage.getItem('jd_cart')) || [];

        /* --- Initialization --- */
        document.addEventListener('DOMContentLoaded', () => {
            updatePriceDisplay();
            updateCartBadge();
            startTimer();
        });

        /* --- Interaction Logic --- */
        function selectSku(type, value, element, multiplier = null) {
            // Visual selection
            const container = element.parentElement;
            const items = container.getElementsByClassName('sku-item');
            for(let item of items) item.classList.remove('selected');
            element.classList.add('selected');

            // Logic update
            if (type === 'flavor') {
                currentProduct.flavor = value;
            } else if (type === 'weight') {
                currentProduct.weight = value;
                if (multiplier) currentProduct.weightMultiplier = multiplier;
            }

            updatePriceDisplay();
        }

        function updateQty(change) {
            let newQty = currentProduct.qty + change;
            if (newQty < 1) newQty = 1;
            if (newQty > 50) newQty = 50; // Max limit

            currentProduct.qty = newQty;
            document.getElementById('qtyInput').value = newQty;
        }

        function updatePriceDisplay() {
            // Calculate unit price based on weight multiplier
            const finalPrice = (basePrice * currentProduct.weightMultiplier).toFixed(2);
            const oldPriceCalc = (finalPrice * 1.3).toFixed(2); // Fake original price

            currentProduct.price = parseFloat(finalPrice); // Update state

            document.getElementById('displayPrice').innerText = finalPrice;
            document.getElementById('oldPrice').innerText = 'Â¥' + oldPriceCalc;
        }

        /* --- Cart Functions --- */
        function addToCart() {
            // Check if same item exists
            const existingItemIndex = cart.findIndex(item => 
                item.flavor === currentProduct.flavor && 
